mapper.select:
select md_class
  from medicine
 where md_no = #{mdNo}

select mi_code
  from medicine
 where md_no = #{mdNo}

select md_trem
  from medicine
 where md_no = #{mdNo}

select md_name
  from medicine
 where md_no =#{mdNo}

mapper.delete:
delete from select_list 
where md_no = #{mdNo}
and chemist_code = #{chemistCode}
